import React, { Component } from 'react';
import axios from 'axios'
import ReactLoading from "react-loading";
import Searchbox from './searchbox'
import Worldbox from './worldbox'
import Worlddisplay from './worldtable'
class Worldapi extends Component{
 constructor(props){
    super(props)
    this.state={
        items:[],
        flag:false,
        field:"",

    }

 }
async componentDidMount(){
    await  axios.get('https://coronavirus-19-api.herokuapp.com/countries/')
     .then((response)=>{
         console.log(response.data)

        this.setState({items:response.data,flag:true})}).catch((err)=>console.log(err))

 }


 onSearchChange=event=>{
  this.setState({field:event.target.value})

   }
 render(){
  var {items,field}=this.state;
  const filterdItem=items.filter(item=>{
   return item.state.toLowerCase().includes(field.toLowerCase())
 })
     return(
         <div>
      {! this.state.flag? (<ReactLoading type='spokes' color='grey' height={100} width={100} />):(
     <div>
     <Worldbox items={this.state.items[0]}/>

     <Searchbox  changer={this.onSearchChange}/>

     <Worlddisplay items={ filterdItem} />
     </div>
     )}
         </div>
     )
 }


}

export default Worldapi;
