    this.state={
        items:[],

    }

 }
 componentDidMount(){
     axios.get('https://coronavirus-19-api.herokuapp.com/countries/')
     .then((response)=>{
         console.log(response.data)

        this.setState({items:response.data})}).catch((err)=>console.log(err))

 }


 render(){
     if(this.state.items[0]){
         const val=<Worlddisplay items={this.state.items}/>
     }
     return(
         <div><h1>hi</h1>
         {val}
         </div>
     )
 }

}

